class PostsController < ApplicationController  before_filter :signed_in_user, only: [:create, :destroy]  def create    @post = current_user.posts.build(params[:post])    if @post.save      redirect_to root_url    else      render "pages/home"    end  end    def show    @post = Post.find(params[:id])  end    def edit  end    def destroy  endend